mapper.sql:
WHERE 1=1

	AND ${SEARCH_KEY} LIKE '%${SEARCH_VALUE}%'


	AND USE_CODE = #{USE_CODE}


	AND LANGUAGE = #{LANGUAGE}


	AND MARKET_YN = #{MARKET_YN}


	AND REG_DATE >= #{SDATE}


	AND REG_DATE <= #{EDATE}



	AND USER_CODE = '03'
	AND USER_STATS_YN = 'Y'


	AND USER_CODE = '03'
	AND USER_STATS_YN = 'N'

mapper.select:
/* getAdminCount */
SELECT * FROM (
	SELECT COUNT(1)totcnt, 1 rownum
	FROM NetMiner.ADMIN_INFO A
	
) NO

/* getAdminInfo */
SELECT A.*
	, DATE_FORMAT(REG_DATE,'%Y-%m-%d')REG_DATES
	, password(#{pwd}) pwd
FROM NetMiner.ADMIN_INFO A
WHERE 1=1
	AND ADMIN_ID = #{ADMIN_ID}
	AND NO = #{NO}

mapper.update:
/* modifyAdminInfo */
UPDATE NetMiner.ADMIN_INFO
	SET UPDATE_DATE = NOW()
	, DESCRIPTION = #{DESCRIPTION}
	, ADMIN_PWD = password(#{ADMIN_PWD})
	WHERE NO=#{NO}

/* insertAdminInfo */
INSERT INTO NetMiner.ADMIN_INFO(ADMIN_CODE, ADMIN_ID, ADMIN_PWD, DESCRIPTION, REG_DATE)
	SELECT '01' ADMIN_CODE, #{ADMIN_ID} ADMIN_ID, password(#{ADMIN_PWD}) ADMIN_PWD, #{DESCRIPTION} DESCRIPTION, now() REG_DATE

mapper.delete:
/* deleteAdminInfo */
DELETE FROM NetMiner.ADMIN_INFO
	WHERE NO=#{NO}

mapper.select:
/* getMemberCount */
SELECT * FROM (
	SELECT COUNT(1)totcnt, 1 rownum
	FROM NetMiner.MEMBER_INFO A
	
	AND USER_CODE='02'
) NO

mapper.update:
/* modifyMemberInfo */
UPDATE NetMiner.MEMBER_INFO
	SET UPDATE_DATE = NOW()
	, USER_CODE= #{USER_CODE}
	, USE_CODE= #{USE_CODE}
	, USER_ID= #{USER_ID}
	, USER_PWD= PASSWORD(#{USER_PWD})
	, LANGUAGE= #{LANGUAGE}
	, NATION= #{NATION}
	, COMPANY= #{COMPANY}
	, MARKET_YN= #{MARKET_YN} , MARKET_DATE = now()
	WHERE NO=#{NO}

/* modifyMemberInfo */
UPDATE NetMiner.MEMBER_INFO_DROP
	SET UPDATE_DATE = NOW()
	, USER_CODE= #{USER_CODE}
	, USE_CODE= #{USE_CODE}
	, USER_ID= #{USER_ID}
	, USER_PWD= PASSWORD(#{USER_PWD})
	, LANGUAGE= #{LANGUAGE}
	, NATION= #{NATION}
	, COMPANY= #{COMPANY}
	, MARKET_YN= #{MARKET_YN} , MARKET_DATE = now()
	WHERE NO=#{NO}

