mapper.select:
 	SELECT `NO`, USER_CODE, USER_ID, USER_PWD, COMPANY, NATION, `LANGUAGE`, USE_CODE, MARKET_YN, GOOGLE_YN
	FROM NetMiner.MEMBER_INFO
	WHERE USER_ID =  #{userId}
	
	AND USER_PWD  = password(#{userPwd})
	
	AND USER_CODE = '02'
	AND USER_STATS_YN = 'N' ;

 	SELECT `NO`, USER_CODE, USER_ID, USER_PWD, COMPANY, NATION, `LANGUAGE`, USE_CODE, MARKET_YN, GOOGLE_YN
	FROM NetMiner.MEMBER_INFO
	WHERE USER_ID =  #{userId}
	AND USER_PWD  = password(#{userPwd})
	AND USER_STATS_YN = 'N'
	;

mapper.insert:
 	INSERT INTO NetMiner.MEMBER_INFO
	( USER_CODE,USER_ID, USER_PWD, COMPANY, NATION, USE_CODE, LANGUAGE ,MARKET_YN ,MARKET_DATE, GOOGLE_YN, LAST_LOGIN_DATE, REG_DATE)
	SELECT * FROM (
		SELECT '02' AS USER_CODE, USER_ID, USER_PWD, COMPANY, NATION, USE_CODE, LANGUAGE ,MARKET_YN ,MARKET_DATE, GOOGLE_YN, now() AS LAST_LOGIN_DATE, REG_DATE
		FROM NetMiner.MEMBER_INFO_DROP WHERE USER_ID =  #{userId}
	) A;

 	INSERT INTO NetMiner.MEMBER_INFO
	( USER_ID, USER_PWD, COMPANY, NATION, USE_CODE, LANGUAGE ,MARKET_YN ,MARKET_DATE, GOOGLE_YN, LAST_LOGIN_DATE, REG_DATE)
	VALUES( 
	#{userId} , PASSWORD(#{userPwd}) , #{company}, 
	#{nation}  , #{useCode} , #{language} ,#{marketYn} , now(), #{googleYn},now(), now()
	);

mapper.update:
UPDATE NetMiner.MEMBER_INFO
SET AUTHKEY = #{authKey}
WHERE USER_ID = #{userId};

mapper.insert:
 	INSERT INTO NetMiner.MEMBER_INFO_TEMP
	( USER_ID, USER_PWD, COMPANY, NATION, USE_CODE, LANGUAGE, REG_DATE )
	VALUES( 
	#{userId} , PASSWORD(#{userPwd}) , #{company}, #{nation}  , #{useCode} , #{language} , NOW()
	);

mapper.select:
 	select * from NetMiner.MEMBER_INFO_TEMP
 	where USER_ID = #{userId}
 	and no = (select MAX(NO) from NetMiner.MEMBER_INFO_TEMP
			 	where USER_ID = #{userId}
			 	ORDER BY REG_DATE DESC);

mapper.insert:
 	INSERT INTO NetMiner.MEMBER_INFO
	( USER_ID, USER_PWD, COMPANY, NATION, USE_CODE, LANGUAGE ,MARKET_YN ,MARKET_DATE,GOOGLE_YN ,LAST_LOGIN_DATE, REG_DATE)
	SELECT USER_ID, USER_PWD, COMPANY, NATION, USE_CODE, `LANGUAGE` ,#{marketYn} AS MARKET_YN , NOW() AS MARKET_DATE,#{googleYn} AS GOOGLE_YN, NOW() AS LAST_LOGIN_DATE, NOW() AS LAST_LOGIN_DATE  
	FROM NetMiner.MEMBER_INFO_TEMP WHERE USER_ID = #{userId} 
	AND NO = (select MAX(NO) from NetMiner.MEMBER_INFO_TEMP
			 	where USER_ID = #{userId}
			 	ORDER BY REG_DATE DESC)
 	;

mapper.update:
 	UPDATE NetMiner.MEMBER_INFO 
 	SET LAST_LOGIN_DATE = NOW()
 	, USER_CODE = '02' 
 	WHERE USER_ID = #{userId}
	and USER_PWD  = password(#{userPwd});

UPDATE NetMiner.MEMBER_INFO
SET USER_PWD = password(#{userPwd})
WHERE USER_ID = #{userId};

UPDATE NetMiner.MEMBER_INFO
SET USER_ID = #{newUserId}
WHERE USER_ID = #{userId};

UPDATE NetMiner.MEMBER_INFO SET
USER_ID = #{userId}, USER_PWD = password(#{userPwd}),
COMPANY = #{company}, NATION = #{nation}, `LANGUAGE` = #{language}, USE_CODE = #{useCode}, MARKET_YN = #{marketYn} WHERE USER_ID = #{oldUserId};

 	INSERT INTO NetMiner.MEMBER_INFO_DROP
	(USER_CODE, USER_ID, USER_PWD, COMPANY, NATION, `LANGUAGE`, USE_CODE, LAST_LOGIN_DATE, MARKET_YN, MARKET_DATE, GOOGLE_YN, REG_DATE, USER_OUT_DATE, USER_STATS_YN, UPDATE_DATE)
	SELECT A.* FROM (
	SELECT '03' AS 'USER_CODE', USER_ID, USER_PWD, COMPANY, NATION, `LANGUAGE`, USE_CODE, LAST_LOGIN_DATE, MARKET_YN, MARKET_DATE, GOOGLE_YN, REG_DATE, NOW() AS 'USER_OUT_DATE', 'N' AS 'USER_STATS_YN', UPDATE_DATE 
	FROM NetMiner.MEMBER_INFO 
	WHERE USER_ID = #{userId}
	) AS A
	;

mapper.delete:
DELETE FROM NetMiner.MEMBER_INFO_DROP
WHERE USE_STATS_YN  = 'Y'
AND TIMESTAMPDIFF(DAY,USER_OUT_DATE,NOW()) = 730;
