mapper.select:
  	SELECT EMAIL_CODE, TITLE, COMMENT
	FROM NetMiner.EMAIL_INFO
	WHERE EMAIL_CODE = #{mailCode};


SELECT AUTH_CODE , END_DATE
FROM NetMiner.CHECK_SEND_AUTH_MAIL_LOG
WHERE USER_ID = #{userId}
AND END_DATE < NOW()
AND EMAIL_CODE IN (
'03', '08'
)
AND NO = (SELECT MAX(NO) FROM NetMiner.CHECK_SEND_AUTH_MAIL_LOG WHERE USER_ID = #{userId});
